 <script>
  $('.tijiao').click(function(){
    //获取省份
    $province1=$('#province1').val();
    //获取市
    $city1=$('#city1').val();
    //获取区
    $district1=$('#district1').val();
    //获取详细地址
    $p_address=$('.p_address').val();
    // alert($p_address);
    //获取收件人
    $p_name=$('.p_name').val();
    //获取电话
    $p_phone=$('.p_phone').val();
    // ajax
    $.get('/addr',{'province':$province1,'city':$city1,'district':$district1,'p_address':$p_address,'p_name':$p_name,'p_phone':$p_phone},function(data){
        if(data==1){
          history.go(0);
        }
    });

  });
  
  


